
public class PrintNumberInWord {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 2;
		switch (number) {
		case 1:
			System.out.println("One");
			break;
		case 2:
			System.out.println("Two");
			break;
		default:
			System.out.println("Other");

		}
	}

}
